import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.util.ReflectionUtils;

import java.lang.reflect.Method;
import java.util.ArrayList;
import java.util.HashSet;
import java.util.List;
import java.util.Set;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private ExampleSpringService exampleSpringService;

    @BeforeEach
    public void setUp() {
        exampleSpringService = new ExampleSpringService();
    }

    @Test
    public void testInspectAllMethods_WithMultipleMethods() {
        // Arrange
        Class<?> targetClass = SampleClass.class;
        List<Method> capturedMethods = new ArrayList<>();
        ExampleSpringService.MethodOperation operation = method -> {
            if (!method.isSynthetic()) {
                capturedMethods.add(method);
            }
        };

        Method[] expectedMethods = ReflectionUtils.getAllDeclaredMethods(targetClass);
        List<Method> filteredExpectedMethods = new ArrayList<>();
        for (Method method : expectedMethods) {
            if (!method.isSynthetic()) {
                filteredExpectedMethods.add(method);
            }
        }

        // Act
        exampleSpringService.inspectAllMethods(targetClass, operation);

        // Assert
        List<String> expectedMethodNames = new ArrayList<>();
        for (Method method : filteredExpectedMethods) {
            expectedMethodNames.add(method.getName());
        }

        List<String> capturedMethodNames = new ArrayList<>();
        for (Method method : capturedMethods) {
            capturedMethodNames.add(method.getName());
        }

        assertTrue(capturedMethodNames.containsAll(expectedMethodNames),
                "Captured methods should contain all expected method names.");
        assertEquals(expectedMethodNames.size(), capturedMethodNames.size(),
                "Number of captured methods should match the expected number.");
    }

    @Test
    public void testInspectAllMethods_WithNoDeclaredMethods() {
        // Arrange
        Class<?> targetClass = EmptyClass.class;
        List<Method> capturedMethods = new ArrayList<>();
        ExampleSpringService.MethodOperation operation = method -> {
            if (!method.isSynthetic()) {
                capturedMethods.add(method);
            }
        };

        // Get all declared methods including inherited ones (e.g., from Object)
        Method[] expectedMethods = ReflectionUtils.getAllDeclaredMethods(targetClass);
        List<Method> filteredExpectedMethods = new ArrayList<>();
        for (Method method : expectedMethods) {
            if (!method.isSynthetic()) {
                filteredExpectedMethods.add(method);
            }
        }

        // Act
        exampleSpringService.inspectAllMethods(targetClass, operation);

        // Assert
        List<String> expectedMethodNames = new ArrayList<>();
        for (Method method : filteredExpectedMethods) {
            expectedMethodNames.add(method.getName());
        }

        List<String> capturedMethodNames = new ArrayList<>();
        for (Method method : capturedMethods) {
            capturedMethodNames.add(method.getName());
        }

        // Since EmptyClass has no declared methods, only inherited Object methods should be captured
        List<String> objectMethodNames = new ArrayList<>();
        for (Method method : ReflectionUtils.getAllDeclaredMethods(Object.class)) {
            if (!method.isSynthetic()) {
                objectMethodNames.add(method.getName());
            }
        }

        assertTrue(capturedMethodNames.containsAll(objectMethodNames),
                "Captured methods should contain all methods inherited from Object.");
        assertEquals(objectMethodNames.size(), capturedMethodNames.size(),
                "Number of captured methods should match the number of Object's methods.");
    }

    @Test
    public void testInspectAllMethods_WithInheritedMethods() {
        // Arrange
        Class<?> targetClass = SubClass.class;
        List<Method> capturedMethods = new ArrayList<>();
        ExampleSpringService.MethodOperation operation = method -> {
            if (!method.isSynthetic()) {
                capturedMethods.add(method);
            }
        };

        // Get all declared methods from targetClass, including inherited ones
        Method[] allDeclaredMethods = ReflectionUtils.getAllDeclaredMethods(targetClass);
        Set<Method> uniqueExpectedMethods = new HashSet<>();
        for (Method method : allDeclaredMethods) {
            if (!method.isSynthetic()) {
                uniqueExpectedMethods.add(method);
            }
        }
        int expectedCount = uniqueExpectedMethods.size();

        // Act
        exampleSpringService.inspectAllMethods(targetClass, operation);

        // Assert
        assertEquals(expectedCount, capturedMethods.size(),
                "Captured methods should match the total number of declared methods in targetClass and its superclasses.");

        // Additionally, verify that specific methods are captured
        Set<String> expectedMethodNames = new HashSet<>();
        for (Method method : uniqueExpectedMethods) {
            expectedMethodNames.add(method.getName());
        }

        Set<String> capturedMethodNames = new HashSet<>();
        for (Method method : capturedMethods) {
            capturedMethodNames.add(method.getName());
        }

        assertTrue(capturedMethodNames.containsAll(expectedMethodNames),
                "Captured methods should contain all expected method names from targetClass and its superclasses.");
    }

    // Sample classes for testing
    public static class SampleClass {
        public void methodOne() {}
        private String methodTwo(int param) { return "test"; }
        protected void methodThree() {}
    }

    public static class EmptyClass {
        // No declared methods
    }

    public static class SuperClass {
        public void superMethod() {}
    }

    public static class SubClass extends SuperClass {
        public void subMethod() {}
    }
}

