import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import org.springframework.beans.factory.config.AbstractFactoryBean;

import java.lang.reflect.Field;

public class ExampleSpringServiceTest {

    @Test
    public void testConfigureOptimizedClassLoader() throws Exception {
        ExampleSpringService service = new ExampleSpringService();
        ClassLoader expectedClassLoader = Thread.currentThread().getContextClassLoader();
        service.configureOptimizedClassLoader();

        // Use reflection to access the protected beanClassLoader field
        Field field = AbstractFactoryBean.class.getDeclaredField("beanClassLoader");
        field.setAccessible(true);
        ClassLoader actualClassLoader = (ClassLoader) field.get(service);

        assertEquals(expectedClassLoader, actualClassLoader, "Bean class loader should be set to the thread's context class loader");
    }
}

