import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    @Test
    public void testQualifiesForAdvising_EligibleClass() {
        // Arrange
        TestableExampleSpringService service = new TestableExampleSpringService(true);
        Class<?> eligibleClass = EligibleComponent.class;

        // Act
        boolean result = service.qualifiesForAdvising(eligibleClass);

        // Assert
        assertTrue(result, "Eligible class should qualify for advising.");
    }

    @Test
    public void testQualifiesForAdvising_IneligibleClass() {
        // Arrange
        TestableExampleSpringService service = new TestableExampleSpringService(false);
        Class<?> ineligibleClass = IneligibleComponent.class;

        // Act
        boolean result = service.qualifiesForAdvising(ineligibleClass);

        // Assert
        assertFalse(result, "Ineligible class should not qualify for advising.");
    }

    @Test
    public void testQualifiesForAdvising_NullClass() {
        // Arrange
        TestableExampleSpringService service = new TestableExampleSpringService(false);
        Class<?> nullClass = null;

        // Act
        boolean result = service.qualifiesForAdvising(nullClass);

        // Assert
        assertFalse(result, "Null class should not qualify for advising.");
    }

    // Helper classes for testing
    private static class EligibleComponent {}
    private static class IneligibleComponent {}

    // Testable subclass to override isEligible method
    private static class TestableExampleSpringService extends ExampleSpringService {
        private final boolean eligible;

        public TestableExampleSpringService(boolean eligible) {
            this.eligible = eligible;
        }

        @Override
        protected boolean isEligible(Class<?> componentClass) {
            return eligible;
        }
    }
}

