    @Test
    public void testGetFieldErrorCount_NoErrors() {
        BindException errors = new BindException(new Object(), "objectName");
        int count = service.getFieldErrorCount(errors);
        assertEquals(0, count);
    }

    @Test
    public void testGetFieldErrorCount_SomeErrors() {
        BindException errors = new BindException(new Object(), "objectName");
        errors.addError(new FieldError("objectName", "field1", "error1"));
        errors.addError(new FieldError("objectName", "field2", "error2"));
        int count = service.getFieldErrorCount(errors);
        assertEquals(2, count);
    }

    @Test
    public void testGetFieldErrorCount_AllFieldsHaveErrors() {
        BindException errors = new BindException(new Object(), "objectName");
        errors.addError(new FieldError("objectName", "field1", "error1"));
        errors.addError(new FieldError("objectName", "field2", "error2"));
        errors.addError(new FieldError("objectName", "field3", "error3"));
        errors.addError(new FieldError("objectName", "field4", "error4"));
        errors.addError(new FieldError("objectName", "field5", "error5"));
        int count = service.getFieldErrorCount(errors);
        assertEquals(5, count);
    }
}

