    @Test
    public void testAreResourcesEquivalent_bothNull() {
        DescriptiveResource resource1 = null;
        DescriptiveResource resource2 = null;
        boolean result = service.areResourcesEquivalent(resource1, resource2);
        assertFalse(result, "Both resources are null, should return false");
    }

    @Test
    public void testAreResourcesEquivalent_resource1Null() {
        DescriptiveResource resource1 = null;
        DescriptiveResource resource2 = new DescriptiveResource("Resource2") {
            @Override
            public InputStream getInputStream() throws IOException {
                return null;
            }
        };
        boolean result = service.areResourcesEquivalent(resource1, resource2);
        assertFalse(result, "First resource is null, should return false");
    }

    @Test
    public void testAreResourcesEquivalent_resource2Null() {
        DescriptiveResource resource1 = new DescriptiveResource("Resource1") {
            @Override
            public InputStream getInputStream() throws IOException {
                return null;
            }
        };
        DescriptiveResource resource2 = null;
        boolean result = service.areResourcesEquivalent(resource1, resource2);
        assertFalse(result, "Second resource is null, should return false");
    }

    @Test
    public void testAreResourcesEquivalent_bothResourcesEqual() {
        DescriptiveResource resource1 = new DescriptiveResource("Resource") {
            @Override
            public InputStream getInputStream() throws IOException {
                return null;
            }
        };
        DescriptiveResource resource2 = resource1;
        boolean result = service.areResourcesEquivalent(resource1, resource2);
        assertTrue(result, "Both resources refer to the same instance, should return true");
    }

    @Test
    public void testAreResourcesEquivalent_resourcesNotEqual() {
        DescriptiveResource resource1 = new DescriptiveResource("Resource1") {
            @Override
            public InputStream getInputStream() throws IOException {
