import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.util.concurrent.*;
import java.util.Set;
import java.util.HashSet;
import java.util.List;
import java.util.ArrayList;

public class ExampleSpringServiceTest {

    @Test
    public void testGenerateAtomicUniqueIdSequential() {
        ExampleSpringService service = new ExampleSpringService();
        assertEquals(1, service.generateAtomicUniqueId(), "First ID should be 1");
        assertEquals(2, service.generateAtomicUniqueId(), "Second ID should be 2");
        assertEquals(3, service.generateAtomicUniqueId(), "Third ID should be 3");
    }

    @Test
    public void testGenerateAtomicUniqueIdConcurrent() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfThreads = 100;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfThreads);
        List<Future<Integer>> futures = new ArrayList<>();

        for (int i = 0; i < numberOfThreads; i++) {
            futures.add(executor.submit(() -> service.generateAtomicUniqueId()));
        }

        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.MINUTES);

        Set<Integer> uniqueIds = new HashSet<>();
        for (Future<Integer> future : futures) {
            uniqueIds.add(future.get());
        }

        assertEquals(numberOfThreads, uniqueIds.size(), "All IDs should be unique");
        for (int i = 1; i <= numberOfThreads; i++) {
            assertTrue(uniqueIds.contains(i), "ID " + i + " should be present");
        }
    }

    @Test
    public void testGenerateAtomicUniqueIdMultipleInvocations() {
        ExampleSpringService service = new ExampleSpringService();
        int totalIds = 1000;
        Set<Integer> uniqueIds = new HashSet<>();

        for (int i = 1; i <= totalIds; i++) {
            int id = service.generateAtomicUniqueId();
            assertEquals(i, id, "ID should be sequential and match the expected value");
            uniqueIds.add(id);
        }

        assertEquals(totalIds, uniqueIds.size(), "All generated IDs should be unique and sequential");
    }
}

