import org.junit.jupiter.api.Test;
import org.springframework.core.MethodParameter;

import java.lang.reflect.Method;
import java.util.List;
import java.util.Map;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    // Helper class with methods to obtain Method instances for testing
    private static class TestService {
        public String simpleMethod() {
            return "test";
        }

        public List<String> genericListMethod() {
            return null;
        }

        public Map<String, Integer> genericMapMethod() {
            return null;
        }

        public void voidMethod() {
            // Does nothing
        }

        public <T> T genericTypeMethod() {
            return null;
        }
    }

    @Test
    public void testCaptureReturnValueType_SimpleType() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("simpleMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        String returnValue = "testString";
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null");
        assertEquals(String.class, methodParameter.getParameterType(), "Return type should be String");
    }

    @Test
    public void testCaptureReturnValueType_GenericListType() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("genericListMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        // Pass null to focus on method's return type rather than runtime type
        Object returnValue = null;
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null");
        assertEquals(List.class, methodParameter.getParameterType(), "Return type should be List");
        assertTrue(methodParameter.getGenericParameterType() instanceof java.lang.reflect.ParameterizedType,
                "Return type should be parameterized");
        java.lang.reflect.ParameterizedType genericType = (java.lang.reflect.ParameterizedType) methodParameter.getGenericParameterType();
        assertEquals(String.class, genericType.getActualTypeArguments()[0], "Generic type should be String");
    }

    @Test
    public void testCaptureReturnValueType_GenericMapType() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("genericMapMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        // Pass null to focus on method's return type rather than runtime type
        Object returnValue = null;
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null");
        assertEquals(Map.class, methodParameter.getParameterType(), "Return type should be Map");
        assertTrue(methodParameter.getGenericParameterType() instanceof java.lang.reflect.ParameterizedType,
                "Return type should be parameterized");
        java.lang.reflect.ParameterizedType genericType = (java.lang.reflect.ParameterizedType) methodParameter.getGenericParameterType();
        assertEquals(String.class, genericType.getActualTypeArguments()[0], "First generic type should be String");
        assertEquals(Integer.class, genericType.getActualTypeArguments()[1], "Second generic type should be Integer");
    }

    @Test
    public void testCaptureReturnValueType_VoidType() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("voidMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        Object returnValue = null;
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null");
        assertEquals(void.class, methodParameter.getParameterType(), "Return type should be void");
    }

    @Test
    public void testCaptureReturnValueType_GenericTypeMethod() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("genericTypeMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        // Pass null to focus on method's return type rather than runtime type
        Object returnValue = null;
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null");
        assertEquals(Object.class, methodParameter.getParameterType(), "Return type should be Object for generic method");
    }

    @Test
    public void testCaptureReturnValueType_NullReturnValue() throws NoSuchMethodException {
        Method method = TestService.class.getMethod("simpleMethod");
        ExampleSpringService service = new ExampleSpringService(method);

        Object returnValue = null;
        MethodParameter methodParameter = service.captureReturnValueType(returnValue);

        assertNotNull(methodParameter, "MethodParameter should not be null even if return value is null");
        assertEquals(String.class, methodParameter.getParameterType(), "Return type should still be String");
    }
}

