import org.springframework.beans.BeanUtils;
import org.springframework.core.convert.ConversionService;

import java.beans.PropertyEditor;
import java.beans.PropertyEditorSupport;

public class ExampleSpringService {

    public PropertyEditor resolveEditor(Class<?> targetType, ConversionService conversionService) {
        // Attempt to find a PropertyEditor by convention
        PropertyEditor editor = BeanUtils.findEditorByConvention(targetType);
        if (editor != null) {
            return editor;
        }

        // If no conventional editor found, create a custom PropertyEditor using ConversionService
        return new PropertyEditorSupport() {
            @Override
            public void setAsText(String text) throws IllegalArgumentException {
                Object converted = conversionService.convert(text, targetType);
                setValue(converted);
            }

            @Override
            public String getAsText() {
                Object value = getValue();
                return (value != null) ? value.toString() : "";
            }
        };
    }
}

