import java.util.concurrent.atomic.AtomicInteger;

public class ExampleSpringService {
    private final DefaultSubscriptionRegistryBenchmark subscriptionRegistryBenchmark;

    public ExampleSpringService() {
        this.subscriptionRegistryBenchmark = new DefaultSubscriptionRegistryBenchmark();
    }

    public void initializeSubscriptionEnvironment(SystemState systemState) {
        subscriptionRegistryBenchmark.doSetup(systemState.getServerState());
    }
}

class DefaultSubscriptionRegistryBenchmark {
    private String contention;
    private String destination;

    public void doSetup(ServerState serverState) {
        switch (this.contention) {
            case "noSubscribers":
                this.destination = "someDestination_withNoSubscribers_" + serverState.uniqueIdGenerator.incrementAndGet();
                break;
            case "sameDestination":
                this.destination = serverState.destinationIds[0];
                break;
            case "none":
                int uniqueNumber = serverState.uniqueIdGenerator.getAndIncrement();
                this.destination = serverState.destinationIds[uniqueNumber % serverState.destinationIds.length];
                break;
            default:
                throw new IllegalStateException();
        }
    }

    // Getters and Setters for contention and destination
    public String getContention() {
        return contention;
    }

    public void setContention(String contention) {
        this.contention = contention;
    }

    public String getDestination() {
        return destination;
    }

    public void setDestination(String destination) {
        this.destination = destination;
    }
}

class SystemState {
    private ServerState serverState;

    public ServerState getServerState() {
        return serverState;
    }

    public void setServerState(ServerState serverState) {
        this.serverState = serverState;
    }
}

class ServerState {
    public AtomicInteger uniqueIdGenerator = new AtomicInteger();
    public String[] destinationIds;

    public ServerState(String[] destinationIds) {
        this.destinationIds = destinationIds;
    }
}

