import org.springframework.context.MessageSource;
import org.springframework.context.support.DelegatingMessageSource;
import org.springframework.stereotype.Service;

import java.util.concurrent.atomic.AtomicReference;

@Service
public class ExampleSpringService {

    private final DelegatingMessageSource messageSource;
    private final AtomicReference<MessageSource> effectiveMessageSource = new AtomicReference<>();

    public ExampleSpringService(DelegatingMessageSource messageSource) {
        this.messageSource = messageSource;
    }

    public MessageSource obtainEffectiveMessageSource() {
        MessageSource cached = effectiveMessageSource.get();
        if (cached != null) {
            return cached;
        }
        MessageSource parent = messageSource.getParentMessageSource();
        if (parent != null) {
            effectiveMessageSource.compareAndSet(null, parent);
            return parent;
        }
        effectiveMessageSource.compareAndSet(null, messageSource);
        return messageSource;
    }
}

