import java.io.IOException;
import java.io.StringWriter;
import java.io.Writer;

public class ExampleSpringService {

    public Writer createIndentedOutputWriter() {
        StringWriter stringWriter = new StringWriter();
        IndentingWriter indentingWriter = new IndentingWriter(stringWriter);
        return indentingWriter;
    }

    // Inner class implementing IndentingWriter
    public static class IndentingWriter extends Writer {
        private final Writer out;
        private int indentLevel = 0;
        private boolean prependIndent = false;
        private static final String INDENT = "    "; // 4 spaces

        public IndentingWriter(Writer out) {
            this.out = out;
        }

        /**
         * Write a new line with indentation.
         */
        public IndentingWriter println() {
            try {
                out.write(System.lineSeparator());
                prependIndent = true;
            } catch (IOException ex) {
                throw new IllegalStateException(ex);
            }
            return this;
        }

        /**
         * Increase indentation level.
         */
        public IndentingWriter indent() {
            indentLevel++;
            return this;
        }

        /**
         * Decrease indentation level.
         */
        public IndentingWriter unindent() {
            if (indentLevel > 0) {
                indentLevel--;
            }
            return this;
        }

        @Override
        public void write(char[] cbuf, int off, int len) throws IOException {
            if (prependIndent) {
                for (int i = 0; i < indentLevel; i++) {
                    out.write(INDENT);
                }
                prependIndent = false;
            }
            out.write(cbuf, off, len);
        }

        @Override
        public void flush() throws IOException {
            out.flush();
        }

        @Override
        public void close() throws IOException {
            out.close();
        }
    }
}

