    @Test
    public void testCreateIndentedOutputWriter() throws Exception {
        ExampleSpringService service = new ExampleSpringService();
        Writer writer = service.createIndentedOutputWriter();

        // Cast to IndentingWriter to access specific methods
        ExampleSpringService.IndentingWriter indentingWriter = (ExampleSpringService.IndentingWriter) writer;

        // Use reflection to access the private 'out' field
        Field outField = ExampleSpringService.IndentingWriter.class.getDeclaredField("out");
        outField.setAccessible(true);
        StringWriter stringWriter = (StringWriter) outField.get(indentingWriter);

        // Perform writing operations
        indentingWriter.write("root:");
        indentingWriter.println();
        indentingWriter.indent();
        indentingWriter.write("child1: value1");
        indentingWriter.println();
        indentingWriter.write("child2: value2");
        indentingWriter.println();
        indentingWriter.indent();
        indentingWriter.write("grandchild: value3");
        indentingWriter.println();
        indentingWriter.unindent();
        indentingWriter.unindent();
        indentingWriter.write("end");

        // Flush and close the writer
        indentingWriter.flush();
        indentingWriter.close();

        // Define the expected output
        String expected = ""
                + "root:" + System.lineSeparator()
                + "    child1: value1" + System.lineSeparator()
                + "    child2: value2" + System.lineSeparator()
                + "        grandchild: value3" + System.lineSeparator()
                + "end";

        // Assert that the output matches the expected string
        assertEquals(expected, stringWriter.toString());
