        Field outField = ExampleSpringService.IndentingWriter.class.getDeclaredField("out");
        outField.setAccessible(true);
        StringWriter stringWriter = (StringWriter) outField.get(indentingWriter);

        // Write without indentation
        indentingWriter.write("level0");
        indentingWriter.println();

        // Indent once
        indentingWriter.indent();
        indentingWriter.write("level1");
        indentingWriter.println();

        // Indent twice
        indentingWriter.indent();
        indentingWriter.write("level2");
        indentingWriter.println();

        // Unindent once
        indentingWriter.unindent();
        indentingWriter.write("back to level1");
        indentingWriter.println();

        // Unindent to level0
        indentingWriter.unindent();
        indentingWriter.write("back to level0");
        indentingWriter.println();

        // Attempt to unindent below level0
        indentingWriter.unindent();
        indentingWriter.write("still level0");
        indentingWriter.println();

        indentingWriter.flush();
        indentingWriter.close();

        String expected = ""
                + "level0" + System.lineSeparator()
                + "    level1" + System.lineSeparator()
                + "        level2" + System.lineSeparator()
                + "    back to level1" + System.lineSeparator()
                + "back to level0" + System.lineSeparator()
                + "still level0" + System.lineSeparator();

        assertEquals(expected, stringWriter.toString());
    }

    @Test
