        // Write second line with indentation
        indentingWriter.indent();
        indentingWriter.write("second line");
        indentingWriter.println();

        // Write third line without changing indentation
        indentingWriter.write("third line");
        indentingWriter.println();

        indentingWriter.flush();
        indentingWriter.close();

        String expected = ""
                + "first line" + System.lineSeparator()
                + "    second line" + System.lineSeparator()
                + "    third line" + System.lineSeparator();

