import java.io.Writer;
import java.io.PrintWriter;
import java.io.OutputStream;

public class ExampleSpringService {
    public Writer createIndentedOutputWriter() {
        return new IndentingWriter(System.out);
    }

    public static class IndentingWriter extends PrintWriter {
        private int indentLevel = 0;
        private static final String INDENT = "    ";

        public IndentingWriter(OutputStream out) {
            super(out, true);
        }

        public void indent() {
            indentLevel++;
        }

        public void unindent() {
            if (indentLevel > 0) indentLevel--;
        }

        @Override
        public void println(String x) {
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < indentLevel; i++) {
                sb.append(INDENT);
            }
            sb.append(x);
            super.println(sb.toString());
        }

        public int getIndentLevel() {
            return indentLevel;
        }
    }
}

