import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

import java.util.HashSet;
import java.util.Set;
import java.util.concurrent.*;
import java.util.stream.Collectors;
import java.util.stream.LongStream;

public class ExampleSpringServiceTest {

    @Test
    public void testSequentialUniqueMessageIds() {
        ExampleSpringService service = new ExampleSpringService();
        long firstId = service.generateUniqueMessageId();
        long secondId = service.generateUniqueMessageId();
        long thirdId = service.generateUniqueMessageId();

        assertEquals(1, firstId, "First ID should be 1");
        assertEquals(2, secondId, "Second ID should be 2");
        assertEquals(3, thirdId, "Third ID should be 3");
    }

    @Test
    public void testUniqueMessageIdsConcurrently() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfThreads = 100;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfThreads);
        Set<Future<Long>> futures = new HashSet<>();

        for (int i = 0; i < numberOfThreads; i++) {
            futures.add(executor.submit(service::generateUniqueMessageId));
        }

        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.SECONDS);

        Set<Long> ids = new HashSet<>();
        for (Future<Long> future : futures) {
            ids.add(future.get());
        }

        assertEquals(numberOfThreads, ids.size(), "All IDs should be unique");
        assertEquals(LongStream.rangeClosed(1, numberOfThreads).boxed().collect(Collectors.toSet()), ids, "IDs should be sequential from 1 to numberOfThreads");
    }

    @Test
    public void testIdOverflow() {
        ExampleSpringService service = new ExampleSpringService();
        // Simulate reaching near the maximum value of AtomicInteger
        for (int i = 0; i < Integer.MAX_VALUE - 1; i++) {
            service.generateUniqueMessageId();
        }
        long lastId = service.generateUniqueMessageId();
