import static org.junit.jupiter.api.Assertions.*;
import org.springframework.cglib.core.Signature;

import java.util.concurrent.Callable;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.Future;

public class ExampleSpringServiceTest {

    @Test
    public void testComputeObjectHash_ReturnsExpectedHashCode() {
        ExampleSpringService service = new ExampleSpringService();
        Signature expectedSignature = new Signature("methodName", "()V");
        int expectedHashCode = expectedSignature.hashCode();
        int actualHashCode = service.computeObjectHash();
        assertEquals(expectedHashCode, actualHashCode, "The computeObjectHash should return the expected hash code.");
    }

    @Test
    public void testComputeObjectHash_IsConsistent() {
        ExampleSpringService service = new ExampleSpringService();
        int firstHash = service.computeObjectHash();
        for (int i = 0; i < 10; i++) {
            int subsequentHash = service.computeObjectHash();
            assertEquals(firstHash, subsequentHash, "computeObjectHash should return consistent hash codes across multiple calls.");
        }
    }

    @Test
    public void testComputeObjectHash_IsThreadSafe() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int expectedHash = service.computeObjectHash();

        int threadCount = 10;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        Callable<Integer> task = service::computeObjectHash;

        Future<Integer>[] futures = new Future[threadCount];
        for (int i = 0; i < threadCount; i++) {
            futures[i] = executor.submit(task);
        }

        for (int i = 0; i < threadCount; i++) {
            assertEquals(expectedHash, futures[i].get(), "computeObjectHash should return the same hash code when called from multiple threads.");
        }

        executor.shutdown();
    }

    @Test
    public void testComputeObjectHash_WithDifferentSignatures() {
        // This test assumes that the signature is initialized differently
        // However, in current implementation, signature is fixed.
        // So, to test different signatures, we might need to extend the service or use reflection.

        // For the purpose of this test, we assume we can create a subclass with different signature
        class CustomExampleSpringService extends ExampleSpringService {
            private final Signature customSignature;

            public CustomExampleSpringService(Signature signature) {
                this.customSignature = signature;
            }

            @Override
            public int computeObjectHash() {
                return customSignature.hashCode();
            }
        }

        Signature differentSignature = new Signature("differentMethod", "()I");
        CustomExampleSpringService customService = new CustomExampleSpringService(differentSignature);
        assertNotEquals(new ExampleSpringService().computeObjectHash(), customService.computeObjectHash(), "Different signatures should produce different hash codes.");
    }
}

