import java.util.Optional;
import org.springframework.expression.spel.support.ReflectivePropertyAccessor;

public class ExampleSpringService {
    protected Optional<Method> locateSetter(String propertyName, Class<?> targetClass) {
        try {
            ReflectivePropertyAccessor accessor = new ReflectivePropertyAccessor();
            Method findSetter = ReflectivePropertyAccessor.class
                .getDeclaredMethod("findSetterForProperty", String.class, Class.class, boolean.class);
            findSetter.setAccessible(true);
            Object result = findSetter.invoke(accessor, propertyName, targetClass, false);
            return Optional.ofNullable((Method) result);
        } catch (Exception e) {
            return Optional.empty();
        }
    }
}

