import java.util.concurrent.atomic.AtomicInteger;

public class ExampleSpringService {

    private ServerState serverState = new ServerState();

    public DestinationAssigner assignDestination(String contentionLevel) {
        FindRequest findRequest = new FindRequest();
        findRequest.setContention(contentionLevel);
        findRequest.doSetup(serverState);
        return new DestinationAssigner(findRequest.destination);
    }

    public static class FindRequest {
        private String contention;
        public String destination;

        public void setContention(String contention) {
            this.contention = contention;
        }

        public void doSetup(ServerState serverState) {
            switch (this.contention) {
                case "noSubscribers":
                    this.destination = "someDestination_withNoSubscribers_" + serverState.uniqueIdGenerator.incrementAndGet();
                    break;
                case "sameDestination":
                    this.destination = serverState.destinationIds[0];
                    break;
                case "none":
                    int uniqueNumber = serverState.uniqueIdGenerator.getAndIncrement();
                    this.destination = serverState.destinationIds[uniqueNumber % serverState.destinationIds.length];
                    break;
                default:
                    throw new IllegalStateException("Invalid contention level: " + this.contention);
            }
        }
    }

    public static class DestinationAssigner {
        private final String destination;

        public DestinationAssigner(String destination) {
            this.destination = destination;
        }

        public String getDestination() {
            return destination;
        }
    }

    public static class ServerState {
        public AtomicInteger uniqueIdGenerator = new AtomicInteger();
        public String[] destinationIds = {"dest1", "dest2", "dest3"};
    }
}

