        AtomicInteger counter = new AtomicInteger(0);
        int mode = "high".equalsIgnoreCase(contentionLevel) ? 0 : ("medium".equalsIgnoreCase(contentionLevel) ? 1 : 2);
        return new DestinationAssigner() {
            @Override
            public String assign(BrokerMessageHandler.FindRequest request, List<String> subscribers) {
                int idx;
                switch (mode) {
                    case 0:
                        idx = Math.abs(counter.getAndIncrement() % destinations.size());
                        break;
                    case 1:
                        String sessionId = request != null ? request.getSessionId() : null;
                        idx = (sessionId != null) ? Math.abs(sessionId.hashCode() % destinations.size()) : 0;
                        break;
                    default:
                        idx = 0;
                }
                return destinations.get(idx);
            }
        };
    }

    public interface DestinationAssigner {
        String assign(BrokerMessageHandler.FindRequest request, List<String> subscribers);
    }
}

