import java.lang.reflect.Method;
import org.springframework.expression.spel.support.ReflectionHelper;

public class ExampleSpringService {
    public Object invokeFunctionWithFlexibleArgs(String functionName, Class<?>[] fixedParamTypes, Object... args) {
        try {
            Method targetMethod = null;
            Method[] methods = this.getClass().getMethods();
            for (Method method : methods) {
                if (!method.getName().equals(functionName)) continue;
                Class<?>[] paramTypes = method.getParameterTypes();
                boolean match = true;
                if (method.isVarArgs()) {
                    if (paramTypes.length != fixedParamTypes.length + 1) continue;
                    for (int i = 0; i < fixedParamTypes.length; i++) {
                        if (!paramTypes[i].equals(fixedParamTypes[i])) {
                            match = false;
                            break;
                        }
                    }
                } else {
                    if (paramTypes.length != fixedParamTypes.length) continue;
                    for (int i = 0; i < fixedParamTypes.length; i++) {
                        if (!paramTypes[i].equals(fixedParamTypes[i])) {
                            match = false;
                            break;
                        }
                    }
                }
                if (match) {
                    targetMethod = method;
                    break;
                }
            }
            if (targetMethod == null) {
                throw new RuntimeException("No such method: " + functionName);
            }
            Object[] invokeArgs = ReflectionHelper.setupArgumentsForVarargsInvocation(targetMethod.getParameterTypes(), args);
            return targetMethod.invoke(this, invokeArgs);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }
}

