import java.util.concurrent.CompletableFuture;
import org.springframework.util.concurrent.ListenableFutureCallback;
import org.springframework.util.concurrent.ListenableFutureCallbackRegistry;

public class ExampleSpringService {

    public CompletableFuture<String> processAsyncResult() {
        CompletableFuture<String> future = new CompletableFuture<>();
        ListenableFutureCallbackRegistry<String> registry = new ListenableFutureCallbackRegistry<>();

        registry.addCallback(new ListenableFutureCallback<String>() {
            @Override
            public void onSuccess(String result) {
                future.complete(result);
            }

            @Override
            public void onFailure(Throwable ex) {
                future.completeExceptionally(ex);
            }
        });

        // Simulate asynchronous processing and trigger success
        new Thread(() -> {
            String result = "Async Result";
            registry.success(result);
        }).start();

        return future;
    }
}

