import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

import java.util.concurrent.CompletableFuture;
import java.util.concurrent.ExecutionException;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;

public class ExampleSpringServiceTest {

    @Test
    public void testProcessAsyncResultSuccess() throws InterruptedException, ExecutionException, TimeoutException {
        ExampleSpringService service = new ExampleSpringService();
        CompletableFuture<String> future = service.processAsyncResult();
        String result = future.get(2, TimeUnit.SECONDS);
        assertEquals("Async Result", result, "The async result should be 'Async Result'");
    }

    @Test
    public void testProcessAsyncResultExceptionally() throws InterruptedException, ExecutionException, TimeoutException {
        ExampleSpringService service = new ExampleSpringServiceWithFailure();
        CompletableFuture<String> future = service.processAsyncResult();
        ExecutionException exception = assertThrows(ExecutionException.class, () -> {
            future.get(2, TimeUnit.SECONDS);
        });
        assertEquals("Simulated Failure", exception.getCause().getMessage(), "The exception message should be 'Simulated Failure'");
    }

    // Helper class to simulate failure
    private static class ExampleSpringServiceWithFailure extends ExampleSpringService {
        @Override
        public CompletableFuture<String> processAsyncResult() {
            CompletableFuture<String> future = new CompletableFuture<>();
            // Simulate asynchronous processing and trigger failure
            new Thread(() -> {
                Throwable ex = new RuntimeException("Simulated Failure");
                future.completeExceptionally(ex);
            }).start();
            return future;
        }
    }
}

