import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;

import java.lang.reflect.Field;

public class ExampleSpringServiceTest {

    private ExampleSpringService service;
    private ExampleSpringService.ServerState serverState;
    private ExampleSpringService.DefaultSubscriptionRegistryBenchmark subscriptionRegistryBenchmark;

    @BeforeEach
    void setUp() throws Exception {
        service = new ExampleSpringService();
        serverState = new ExampleSpringService.ServerState();

        // Access the private subscriptionRegistryBenchmark field via reflection
        Field benchmarkField = ExampleSpringService.class.getDeclaredField("subscriptionRegistryBenchmark");
        benchmarkField.setAccessible(true);
        subscriptionRegistryBenchmark = (ExampleSpringService.DefaultSubscriptionRegistryBenchmark) benchmarkField.get(service);
    }

    @Test
    void testConfigureDynamicDestination_noSubscribers() {
        // Set contention to "noSubscribers"
        subscriptionRegistryBenchmark.setContention("noSubscribers");

        // Configure dynamic destination
        service.configureDynamicDestination(serverState);

        // Expected destination
        String expectedDestination = "someDestination_withNoSubscribers_1";
        String actualDestination = subscriptionRegistryBenchmark.getDestination();

        assertEquals(expectedDestination, actualDestination, "Destination should match the expected value for 'noSubscribers' contention.");
    }

    @Test
    void testConfigureDynamicDestination_sameDestination() {
        // Set contention to "sameDestination"
        subscriptionRegistryBenchmark.setContention("sameDestination");

        // Configure dynamic destination
        service.configureDynamicDestination(serverState);

        // Expected destination
        String expectedDestination = serverState.destinationIds[0];
        String actualDestination = subscriptionRegistryBenchmark.getDestination();

        assertEquals(expectedDestination, actualDestination, "Destination should match the first destination ID for 'sameDestination' contention.");
    }

    @Test
    void testConfigureDynamicDestination_none() {
        // Set contention to "none"
        subscriptionRegistryBenchmark.setContention("none");

        // Configure dynamic destination multiple times to test cycling through destinationIds
        service.configureDynamicDestination(serverState);
        String expectedDestination1 = serverState.destinationIds[0];
        String actualDestination1 = subscriptionRegistryBenchmark.getDestination();
        assertEquals(expectedDestination1, actualDestination1, "First destination should be the first in destinationIds for 'none' contention.");

        service.configureDynamicDestination(serverState);
        String expectedDestination2 = serverState.destinationIds[1];
        String actualDestination2 = subscriptionRegistryBenchmark.getDestination();
        assertEquals(expectedDestination2, actualDestination2, "Second destination should be the second in destinationIds for 'none' contention.");

        service.configureDynamicDestination(serverState);
        String expectedDestination3 = serverState.destinationIds[2];
        String actualDestination3 = subscriptionRegistryBenchmark.getDestination();
        assertEquals(expectedDestination3, actualDestination3, "Third destination should be the third in destinationIds for 'none' contention.");

        service.configureDynamicDestination(serverState);
        String expectedDestination4 = serverState.destinationIds[0];
        String actualDestination4 = subscriptionRegistryBenchmark.getDestination();
        assertEquals(expectedDestination4, actualDestination4, "Destination should cycle back to the first in destinationIds after exceeding the array length for 'none' contention.");
    }

    @Test
    void testConfigureDynamicDestination_invalidContention() {
        // Set contention to an invalid value
        subscriptionRegistryBenchmark.setContention("invalidContention");

        // Expect IllegalStateException when configuring dynamic destination
        assertThrows(IllegalStateException.class, () -> {
            service.configureDynamicDestination(serverState);
        }, "An IllegalStateException should be thrown for invalid contention values.");
    }
}

