import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.lang.annotation.*;
import java.lang.reflect.Method;
import org.springframework.core.annotation.AliasFor;

// Sample annotations for testing
@Retention(RetentionPolicy.RUNTIME)
@Target({ElementType.METHOD, ElementType.ANNOTATION_TYPE})
@interface TestAnnotation {
    String value() default "default";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.ANNOTATION_TYPE)
@TestAnnotation
@interface MetaAnnotation {
    String metaValue() default "meta";
}

@Retention(RetentionPolicy.RUNTIME)
@Target(ElementType.METHOD)
@MetaAnnotation
@interface AnnotatedWithMeta {
}

public class ExampleSpringServiceTest {

    // Class under test
    private final ExampleSpringService service = new ExampleSpringService();

    // Helper classes for testing inheritance
    public static class SuperClass {
        @TestAnnotation("superMethod")
        public void annotatedMethod() {}

        public void nonAnnotatedMethod() {}
    }

    public static class SubClass extends SuperClass {
        @Override
        public void annotatedMethod() {}

        @AnnotatedWithMeta
        public void metaAnnotatedMethod() {}
    }

    @Test
    public void testRetrieveDirectAnnotation() throws NoSuchMethodException {
        Method method = SuperClass.class.getMethod("annotatedMethod");
        TestAnnotation annotation = service.retrieveAnnotation(method, TestAnnotation.class);
        assertNotNull(annotation, "Annotation should be present");
        assertEquals("superMethod", annotation.value(), "Annotation value should match");
    }

    @Test
    public void testRetrieveInheritedAnnotation() throws NoSuchMethodException {
        Method method = SubClass.class.getMethod("annotatedMethod");
        TestAnnotation annotation = service.retrieveAnnotation(method, TestAnnotation.class);
        assertNotNull(annotation, "Inherited annotation should be present");
        assertEquals("superMethod", annotation.value(), "Inherited annotation value should match");
    }

    @Test
    public void testRetrieveMetaAnnotation() throws NoSuchMethodException {
        Method method = SubClass.class.getMethod("metaAnnotatedMethod");
        TestAnnotation annotation = service.retrieveAnnotation(method, TestAnnotation.class);
        assertNotNull(annotation, "Meta-annotation should be present");
        assertEquals("default", annotation.value(), "Meta-annotation value should match");
    }

    @Test
    public void testAnnotationNotPresent() throws NoSuchMethodException {
        Method method = SuperClass.class.getMethod("nonAnnotatedMethod");
        TestAnnotation annotation = service.retrieveAnnotation(method, TestAnnotation.class);
        assertNull(annotation, "Annotation should not be present");
    }

    @Test
    public void testRetrieveAnnotationWithDifferentAnnotationClass() throws NoSuchMethodException {
        Method method = SuperClass.class.getMethod("annotatedMethod");
        // Attempt to retrieve a different annotation
        Deprecated deprecated = service.retrieveAnnotation(method, Deprecated.class);
        assertNull(deprecated, "Different annotation should not be present");
    }

    @Test
    public void testRetrieveAnnotationOnClassMethod() throws NoSuchMethodException {
        Method method = ExampleSpringService.class.getMethod("retrieveAnnotation", Method.class, Class.class);
        Deprecated deprecated = service.retrieveAnnotation(method, Deprecated.class);
        assertNull(deprecated, "Annotation should not be present on retrieveAnnotation method");
    }

    @Test
    public void testRetrieveAnnotationWithNullMethod() {
        assertThrows(IllegalArgumentException.class, () -> {
            service.retrieveAnnotation(null, TestAnnotation.class);
        }, "Should throw IllegalArgumentException when method is null");
    }

    @Test
    public void testRetrieveAnnotationWithNullAnnotationClass() throws NoSuchMethodException {
        Method method = SuperClass.class.getMethod("annotatedMethod");
        TestAnnotation annotation = service.retrieveAnnotation(method, null);
        assertNull(annotation, "Annotation should be null when annotationClass is null");
    }
}

