import org.springframework.util.LinkedMultiValueMap;
import org.springframework.util.MultiValueMap;

public class ExampleSpringService {
    public MultiValueMap<String, String> fetchSubscriptions(ServerState serverState, FindRequest request) {
        MultiValueMap<String, String> result = new LinkedMultiValueMap<>();
        if (request == null || request.destination == null || request.destination.isEmpty()) {
            return result;
        }
        if (serverState != null && serverState.subscriptions != null) {
            for (String user : serverState.subscriptions.keySet()) {
                for (String dest : serverState.subscriptions.get(user)) {
                    if (dest.equals(request.destination)) {
                        result.add(dest, user);
                    }
                }
            }
        }
        return result;
    }

    public static class ServerState {
        public MultiValueMap<String, String> subscriptions = new LinkedMultiValueMap<>();
    }

    public static class FindRequest {
        public String destination;
        public FindRequest(String destination) {
            this.destination = destination;
        }
    }
}

