import org.springframework.validation.method.ParameterValidationResult;
import org.springframework.context.MessageSourceResolvable;
import org.springframework.core.MethodParameter;

public class ExampleSpringService {

    public static class ValidationErrorDetail {
        private final String objectName;
        private final String code;
        private final String message;

        public ValidationErrorDetail(String objectName, String code, String message) {
            this.objectName = objectName;
            this.code = code;
            this.message = message;
        }

        public String getObjectName() {
            return objectName;
        }

        public String getCode() {
            return code;
        }

        public String getMessage() {
            return message;
        }
    }

    public List<ValidationErrorDetail> extractValidationFeedback() {
        List<ValidationErrorDetail> errorDetails = new ArrayList<>();
        // There is no static method to get all ParameterValidationResult in Spring,
        // so typically you would have a list passed in or available via context.
        // For this implementation, we assume this list is available as a bean field or injected.
        // Here, we'll use an empty list as placeholder.
        List<ParameterValidationResult> validationResults = new ArrayList<>();
        for (ParameterValidationResult result : validationResults) {
            List<MessageSourceResolvable> errors = result.getResolvableErrors();
            MethodParameter param = result.getMethodParameter();
            String objectName = param != null ? param.getParameterName() : null;
            if (errors != null) {
                for (MessageSourceResolvable error : errors) {
                    String code = null;
                    String[] codes = error.getCodes();
                    if (codes != null && codes.length > 0) {
                        code = codes[0];
                    }
                    String message = error.getDefaultMessage();
                    errorDetails.add(new ValidationErrorDetail(objectName, code, message));
                }
            }
        }
        return errorDetails;
    }
}

