import static org.junit.jupiter.api.Assertions.assertEquals;
import org.springframework.ui.ModelMap;
import java.util.HashMap;
import java.util.Map;

public class ExampleSpringServiceTest {

    private final ExampleSpringService service = new ExampleSpringService();

    @Test
    public void testConsolidatePreferences_BothPreferencesNonNull_WithOverlappingKeys() {
        Map<String, String> defaultPreferences = new HashMap<>();
        defaultPreferences.put("theme", "light");
        defaultPreferences.put("language", "en");
        defaultPreferences.put("notifications", "enabled");

        Map<String, String> userPreferences = new HashMap<>();
        userPreferences.put("language", "fr");
        userPreferences.put("fontSize", "14px");

        ModelMap result = service.consolidatePreferences(defaultPreferences, userPreferences);

        assertEquals(4, result.size());
        assertEquals("fr", result.get("language"));
        assertEquals("light", result.get("theme"));
        assertEquals("enabled", result.get("notifications"));
        assertEquals("14px", result.get("fontSize"));
    }

    @Test
    public void testConsolidatePreferences_UserPreferencesNull() {
        Map<String, String> defaultPreferences = new HashMap<>();
        defaultPreferences.put("theme", "dark");
        defaultPreferences.put("language", "en");

        ModelMap result = service.consolidatePreferences(defaultPreferences, null);

        assertEquals(2, result.size());
        assertEquals("dark", result.get("theme"));
        assertEquals("en", result.get("language"));
    }

    @Test
