import org.junit.jupiter.api.Test;
import org.springframework.cache.Cache.ValueWrapper;
import org.springframework.cache.support.SimpleValueWrapper;
import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private final ExampleSpringService service = new ExampleSpringService();

    @Test
    public void testComputeSessionCacheHash_WithStringValue() {
        String sessionData = "userSessionData";
        ValueWrapper wrapper = new SimpleValueWrapper(sessionData);
        int expectedHash = sessionData.hashCode();
        int actualHash = service.computeSessionCacheHash(wrapper);
        assertEquals(expectedHash, actualHash, "Hash codes should match for String value");
    }

    @Test
    public void testComputeSessionCacheHash_WithIntegerValue() {
        Integer sessionData = 12345;
        ValueWrapper wrapper = new SimpleValueWrapper(sessionData);
        int expectedHash = sessionData.hashCode();
        int actualHash = service.computeSessionCacheHash(wrapper);
        assertEquals(expectedHash, actualHash, "Hash codes should match for Integer value");
    }

    @Test
    public void testComputeSessionCacheHash_WithNullValue() {
        ValueWrapper wrapper = new SimpleValueWrapper(null);
        int expectedHash = 0;
        int actualHash = service.computeSessionCacheHash(wrapper);
        assertEquals(expectedHash, actualHash, "Hash code should be 0 for null value");
    }

    @Test
    public void testComputeSessionCacheHash_ConsistentHashCode() {
        String sessionData = "consistentSessionData";
        ValueWrapper wrapper1 = new SimpleValueWrapper(sessionData);
        ValueWrapper wrapper2 = new SimpleValueWrapper(sessionData);
        int hash1 = service.computeSessionCacheHash(wrapper1);
        int hash2 = service.computeSessionCacheHash(wrapper2);
        assertEquals(hash1, hash2, "Hash codes should be consistent for identical values");
    }

    @Test
    public void testComputeSessionCacheHash_DifferentValues() {
        String sessionData1 = "sessionDataOne";
        String sessionData2 = "sessionDataTwo";
        ValueWrapper wrapper1 = new SimpleValueWrapper(sessionData1);
        ValueWrapper wrapper2 = new SimpleValueWrapper(sessionData2);
        int hash1 = service.computeSessionCacheHash(wrapper1);
        int hash2 = service.computeSessionCacheHash(wrapper2);
        assertNotEquals(hash1, hash2, "Hash codes should differ for different values");
    }

    @Test
    public void testComputeSessionCacheHash_InvalidValueWrapper() {
        ValueWrapper invalidWrapper = new ValueWrapper() {
            @Override
            public Object get() {
                return "invalidWrapper";
            }
        };
        assertThrows(ClassCastException.class, () -> {
            service.computeSessionCacheHash(invalidWrapper);
        }, "Should throw ClassCastException for non-SimpleValueWrapper instances");
    }
}

