    public int computeSessionCacheHash(ValueWrapper sessionWrapper) {
        if (sessionWrapper instanceof SimpleValueWrapper) {
            return sessionWrapper.hashCode();
        }
        if (sessionWrapper == null || sessionWrapper.get() == null) {
            return 0;
        }
        Object value = sessionWrapper.get();
        if (value instanceof SimpleValueWrapper) {
            return value.hashCode();
        }
