import org.springframework.util.xml.StaxUtils;
import javax.xml.stream.XMLInputFactory;
import javax.xml.stream.XMLStreamException;
import javax.xml.stream.XMLStreamReader;
import java.io.StringReader;

public class ExampleSpringService {
    private XMLStreamReader xmlStreamReader;

    public ExampleSpringService(String xmlContent) throws XMLStreamException {
        XMLInputFactory factory = StaxUtils.createDefensiveInputFactory();
        this.xmlStreamReader = factory.createXMLStreamReader(new StringReader(xmlContent));
    }

    public String extractTextContent() throws XMLStreamException {
        if (xmlStreamReader.hasNext()) {
            xmlStreamReader.next();
            if (xmlStreamReader.isStartElement()) {
                return xmlStreamReader.getElementText();
            } else {
