import org.springframework.beans.factory.BeanDefinitionStoreException;
import org.springframework.beans.factory.support.DefaultListableBeanFactory;
import org.springframework.beans.factory.support.PropertiesBeanDefinitionReader;

import java.io.ByteArrayInputStream;
import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.util.Properties;
import java.util.ResourceBundle;

public class ExampleSpringService {

    public int registerBeanDefinitions(ResourceBundle rb, String prefix) throws BeanDefinitionStoreException {
        Properties filteredProps = new Properties();
        for (String key : rb.keySet()) {
            if (key.startsWith(prefix)) {
                filteredProps.put(key, rb.getString(key));
            }
        }
        try (ByteArrayOutputStream baos = new ByteArrayOutputStream()) {
            filteredProps.store(baos, null);
            ByteArrayInputStream bais = new ByteArrayInputStream(baos.toByteArray());
            ResourceBundle filteredRb = new java.util.PropertyResourceBundle(bais);
            DefaultListableBeanFactory beanFactory = new DefaultListableBeanFactory();
            PropertiesBeanDefinitionReader reader = new PropertiesBeanDefinitionReader(beanFactory);
            return reader.registerBeanDefinitions(filteredRb);
        } catch (IOException e) {
            throw new BeanDefinitionStoreException("Failed to filter ResourceBundle", e);
        }
    }
}

