import org.springframework.beans.PropertyAccessor;
import org.springframework.beans.BeanWrapperImpl;

public class ExampleSpringService {
    public PropertyAccessor createNestedPropertyAccessor(Object bean, String propertyPath) {
        if (propertyPath == null || propertyPath.isEmpty()) {
            return new BeanWrapperImpl(bean);
        }
        Object current = bean;
        String[] properties = propertyPath.split("\\.");
        for (String property : properties) {
            if (current == null) {
                return null;
            }
            BeanWrapperImpl wrapper = new BeanWrapperImpl(current);
            current = wrapper.getPropertyValue(property);
        }
        if (current == null) {
            return null;
        }
        return new BeanWrapperImpl(current);
    }
}

