import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.beans.BeansException;
import org.springframework.beans.factory.BeanFactory;
import org.springframework.beans.factory.support.DefaultListableBeanFactory;
import org.springframework.scheduling.TaskScheduler;
import org.springframework.scheduling.concurrent.ThreadPoolTaskScheduler;

public class ExampleSpringServiceTest {

    private TestExampleSpringService exampleSpringService;
    private DefaultListableBeanFactory beanFactory;
    private TaskScheduler schedulerOne;
    private TaskScheduler schedulerTwo;
    private TaskScheduler defaultScheduler;

    // Inner class to override determineDefaultScheduler
    private class TestExampleSpringService extends ExampleSpringService {
        private TaskScheduler overriddenDefaultScheduler;

        public void setOverriddenDefaultScheduler(TaskScheduler scheduler) {
            this.overriddenDefaultScheduler = scheduler;
        }

        @Override
        protected TaskScheduler determineDefaultScheduler() {
            if (overriddenDefaultScheduler != null) {
                return overriddenDefaultScheduler;
            } else {
                throw new IllegalStateException("No TaskScheduler beans found in the context.");
            }
        }
    }

    @BeforeEach
    public void setUp() {
        // Initialize TaskScheduler instances
        schedulerOne = new ThreadPoolTaskScheduler();
        ((ThreadPoolTaskScheduler) schedulerOne).afterPropertiesSet();
        schedulerTwo = new ThreadPoolTaskScheduler();
        ((ThreadPoolTaskScheduler) schedulerTwo).afterPropertiesSet();
        defaultScheduler = schedulerOne; // Assume schedulerOne is the default

        // Initialize BeanFactory and register schedulers
        beanFactory = new DefaultListableBeanFactory();
        beanFactory.registerSingleton("schedulerOne", schedulerOne);
        beanFactory.registerSingleton("schedulerTwo", schedulerTwo);

        // Initialize the service and set the overridden default scheduler
        exampleSpringService = new TestExampleSpringService();
        exampleSpringService.setOverriddenDefaultScheduler(defaultScheduler);
        exampleSpringService.setBeanFactory(beanFactory);
    }

    @Test
    public void testSelectOptimalScheduler_WithMultipleSchedulers() {
        // Act
        TaskScheduler selectedScheduler = exampleSpringService.selectOptimalScheduler();

        // Assert
        assertEquals(defaultScheduler, selectedScheduler, "The selected scheduler should be the default scheduler.");
    }

    @Test
    public void testSelectOptimalScheduler_WithSingleScheduler() {
        // Arrange
        beanFactory = new DefaultListableBeanFactory();
        beanFactory.registerSingleton("defaultScheduler", defaultScheduler);
        exampleSpringService.setBeanFactory(beanFactory);
        exampleSpringService.setOverriddenDefaultScheduler(defaultScheduler);

        // Act
        TaskScheduler selectedScheduler = exampleSpringService.selectOptimalScheduler();

        // Assert
        assertEquals(defaultScheduler, selectedScheduler, "The selected scheduler should be the only available scheduler.");
    }

    @Test
    public void testSelectOptimalScheduler_NoSchedulers() {
        // Arrange
        beanFactory = new DefaultListableBeanFactory();
        exampleSpringService.setBeanFactory(beanFactory);
        exampleSpringService.setOverriddenDefaultScheduler(null);

        // Act & Assert
        IllegalStateException exception = assertThrows(IllegalStateException.class, () -> {
            exampleSpringService.selectOptimalScheduler();
        });
        assertEquals("No TaskScheduler beans found in the context.", exception.getMessage());
    }
}

