import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.util.concurrent.*;
import java.util.HashSet;
import java.util.Set;
import java.util.List;
import java.util.ArrayList;

public class ExampleSpringServiceTest {

    @Test
    public void testGenerateUniqueMessageIdSequential() {
        ExampleSpringService service = new ExampleSpringService();
        String firstId = service.generateUniqueMessageId();
        String secondId = service.generateUniqueMessageId();
        String thirdId = service.generateUniqueMessageId();

        assertEquals("1", firstId, "First ID should be '1'");
        assertEquals("2", secondId, "Second ID should be '2'");
        assertEquals("3", thirdId, "Third ID should be '3'");
    }

    @Test
    public void testGenerateUniqueMessageIdConcurrent() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int threadCount = 100;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        Set<String> uniqueIds = ConcurrentHashMap.newKeySet();
        CountDownLatch latch = new CountDownLatch(threadCount);
        List<Future<?>> futures = new ArrayList<>();

        for (int i = 0; i < threadCount; i++) {
            futures.add(executor.submit(() -> {
                try {
                    String id = service.generateUniqueMessageId();
                    uniqueIds.add(id);
                } finally {
                    latch.countDown();
                }
            }));
        }

        latch.await();
        executor.shutdown();

        assertEquals(threadCount, uniqueIds.size(), "All generated IDs should be unique");

        // Additionally, verify that all IDs are sequential starting from 1
        Set<Integer> idNumbers = new HashSet<>();
        for (String id : uniqueIds) {
            idNumbers.add(Integer.parseInt(id));
        }
        for (int i = 1; i <= threadCount; i++) {
            assertTrue(idNumbers.contains(i), "ID " + i + " should be present");
        }
    }
}

