import org.springframework.scheduling.concurrent.ThreadPoolTaskScheduler;
import org.springframework.stereotype.Service;
import java.util.concurrent.Delayed;
import java.util.concurrent.ScheduledFuture;

@Service
public class ExampleSpringService {

    private final CustomThreadPoolTaskScheduler taskScheduler;

    public ExampleSpringService() {
        this.taskScheduler = new CustomThreadPoolTaskScheduler();
        this.taskScheduler.initialize();
    }

    public int prioritizeTaskExecution(Delayed task) {
        return taskScheduler.compareTo(task);
    }

    private static class CustomThreadPoolTaskScheduler extends ThreadPoolTaskScheduler implements Comparable<Delayed> {

        private ScheduledFuture<?> future;

        public void setFuture(ScheduledFuture<?> future) {
            this.future = future;
        }

        @Override
        public int compareTo(Delayed o) {
            if (this.future == null) {
                return 0;
            }
            return this.future.compareTo(o);
        }
    }
}

