            Field scheduledTaskField = ThreadPoolTaskScheduler.class.getDeclaredField("scheduledFutureTask");
            scheduledTaskField.setAccessible(true);
            Object scheduledTask = scheduledTaskField.get(task);
            if (scheduledTask != null && scheduledTask instanceof Comparable) {
                return ((Comparable) scheduledTask).compareTo(task);
            }
        } catch (NoSuchFieldException e) {
            // Field not found, ignore
        } catch (IllegalAccessException e) {
            // Cannot access field, ignore
        }
        return 0;
    }
