import java.io.IOException;
import java.nio.ByteBuffer;
import java.nio.channels.WritableByteChannel;
import java.nio.file.Path;
import org.springframework.core.io.PathResource;

public class ExampleSpringService {

    public WritableByteChannel createSafeWriteChannel(Path filePath) throws IOException {
        PathResource pathResource = new PathResource(filePath);
        WritableByteChannel channel = pathResource.writableChannel();
        return new ThreadSafeWritableByteChannel(channel);
    }

    private static class ThreadSafeWritableByteChannel implements WritableByteChannel {
        private final WritableByteChannel channel;
        private final Object lock = new Object();

        public ThreadSafeWritableByteChannel(WritableByteChannel channel) {
            this.channel = channel;
        }

        @Override
        public int write(ByteBuffer src) throws IOException {
            synchronized (lock) {
                return channel.write(src);
            }
        }

        @Override
        public boolean isOpen() {
            return channel.isOpen();
        }

        @Override
        public void close() throws IOException {
            synchronized (lock) {
                channel.close();
            }
        }
    }
}

