public static <E> E findFirstMatch(Collection<?> source, Collection<E> candidates) {
	if (isEmpty(source) || isEmpty(candidates)) {
		return null;
	}
	for (E candidate : candidates) {
		if (source.contains(candidate)) {
			return candidate;
		}
	}
	return null;
}

