import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import static org.junit.jupiter.api.Assertions.*;
import java.util.Set;
import java.util.HashSet;
import java.util.concurrent.*;
import java.util.stream.Collectors;
import java.util.stream.IntStream;

public class ExampleSpringServiceTest {

    private ExampleSpringService service;

    @BeforeEach
    public void setUp() {
        service = new ExampleSpringService();
    }

    @Test
    public void testGenerateUniqueMessageId_SingleThread() {
        String firstId = service.generateUniqueMessageId();
        String secondId = service.generateUniqueMessageId();
        String thirdId = service.generateUniqueMessageId();

        assertNotNull(firstId, "First ID should not be null");
        assertNotNull(secondId, "Second ID should not be null");
        assertNotNull(thirdId, "Third ID should not be null");

        assertEquals("1", firstId, "First ID should be '1'");
        assertEquals("2", secondId, "Second ID should be '2'");
        assertEquals("3", thirdId, "Third ID should be '3'");
    }

    @Test
    public void testGenerateUniqueMessageId_MultipleThreads() throws InterruptedException, ExecutionException {
        int numberOfThreads = 100;
        ExecutorService executor = Executors.newFixedThreadPool(10);
        Set<Future<String>> futures = new HashSet<>();

        for (int i = 0; i < numberOfThreads; i++) {
            futures.add(executor.submit(() -> service.generateUniqueMessageId()));
        }

        executor.shutdown();
        boolean terminated = executor.awaitTermination(1, TimeUnit.MINUTES);
        assertTrue(terminated, "Executor did not terminate in the expected time");

        Set<String> uniqueIds = new HashSet<>();
        for (Future<String> future : futures) {
            String id = future.get();
            assertNotNull(id, "Generated ID should not be null");
            uniqueIds.add(id);
        }

        assertEquals(numberOfThreads, uniqueIds.size(), "All IDs should be unique");
        assertTrue(uniqueIds.contains("1"), "IDs should start from '1'");
        assertTrue(uniqueIds.contains(String.valueOf(numberOfThreads)), "IDs should go up to the number of threads");
    }

    @Test
    public void testGenerateUniqueMessageId_SequentialCalls() {
        int numberOfCalls = 1000;
        Set<String> uniqueIds = IntStream.rangeClosed(1, numberOfCalls)
                                         .mapToObj(i -> service.generateUniqueMessageId())
                                         .collect(Collectors.toSet());

        assertEquals(numberOfCalls, uniqueIds.size(), "All sequential IDs should be unique");
        assertTrue(uniqueIds.contains("1"), "IDs should start from '1'");
        assertTrue(uniqueIds.contains(String.valueOf(numberOfCalls)), "IDs should go up to the number of calls");
    }

    @Test
    public void testGenerateUniqueMessageId_NoExposedInternalState() {
        // Since the internal state is not exposed, we can only test that the IDs are generated correctly
        String id1 = service.generateUniqueMessageId();
        String id2 = service.generateUniqueMessageId();

        assertNotEquals(id1, id2, "Consecutive IDs should be different");
    }
}

