import static org.junit.jupiter.api.Assertions.*;
import org.springframework.core.annotation.AnnotatedElementUtils;
import java.lang.annotation.*;

public class ExampleSpringServiceTest {

    // Define the ExampleSpringService to be tested
    private final ExampleSpringService service = new ExampleSpringService();

    // Custom annotation for testing
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.TYPE)
    public @interface Config {
        String value() default "default";
    }

    // Meta-annotation that uses Config
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.TYPE)
    @Config(value = "meta")
    public @interface MetaConfig {
        String additional() default "additional";
    }

    // Class directly annotated with Config
    @Config(value = "direct")
    public static class DirectAnnotatedClass {
    }

    // Class annotated with MetaConfig
    @MetaConfig
    public static class MetaAnnotatedClass {
    }

    // Superclass annotated with Config
    @Config(value = "superclass")
    public static class SuperClass {
    }

    // Subclass inheriting from SuperClass
    public static class SubClass extends SuperClass {
    }

    // Class without any annotations
    public static class NoAnnotatedClass {
    }

    @Test
    public void testResolveConsolidatedConfig_DirectAnnotation() {
        Config config = service.resolveConsolidatedConfig(DirectAnnotatedClass.class, Config.class);
        assertNotNull(config, "Config annotation should be present");
        assertEquals("direct", config.value(), "Config value should match the direct annotation");
    }

    @Test
    public void testResolveConsolidatedConfig_MetaAnnotation() {
        Config config = service.resolveConsolidatedConfig(MetaAnnotatedClass.class, Config.class);
        assertNotNull(config, "Config annotation should be present via meta-annotation");
        assertEquals("meta", config.value(), "Config value should be merged from meta-annotation");
    }

    @Test
    public void testResolveConsolidatedConfig_InheritedAnnotation() {
        Config config = service.resolveConsolidatedConfig(SubClass.class, Config.class);
        assertNotNull(config, "Config annotation should be inherited from superclass");
        assertEquals("superclass", config.value(), "Config value should match the superclass annotation");
    }

    @Test
    public void testResolveConsolidatedConfig_NoAnnotation() {
        Config config = service.resolveConsolidatedConfig(NoAnnotatedClass.class, Config.class);
        assertNull(config, "Config annotation should not be present");
    }

    @Test
    public void testResolveConsolidatedConfig_MergedAttributes() {
        // Define a meta-annotation with overlapping attributes if needed
        // For this example, Config has only one attribute, so we'll assume merging is based on presence
        // This test ensures that meta-annotations are correctly merged
        Config config = service.resolveConsolidatedConfig(MetaAnnotatedClass.class, Config.class);
        assertNotNull(config, "Config annotation should be present via meta-annotation");
        assertEquals("meta", config.value(), "Config value should be correctly merged from meta-annotation");
    }
}

