import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.util.MultiValueMap;

import java.lang.reflect.Field;
import java.util.AbstractMap;
import java.util.List;
import java.util.Map;
import java.util.Set;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private ExampleSpringService service;

    @BeforeEach
    public void setUp() {
        service = new ExampleSpringService();
    }

    @Test
    public void testGetCategoryEntries_Empty() {
        Set<Map.Entry<String, List<ExampleSpringService.Product>>> entries = service.getCategoryEntries();
        assertNotNull(entries, "Entries should not be null");
        assertTrue(entries.isEmpty(), "Category entries should be empty");
    }

    @Test
    public void testGetCategoryEntries_WithEntries() {
        // Add categories and products using reflection
        try {
            Field categoryMapField = ExampleSpringService.class.getDeclaredField("categoryMap");
            categoryMapField.setAccessible(true);
            @SuppressWarnings("unchecked")
            MultiValueMap<String, ExampleSpringService.Product> categoryMap =
                    (MultiValueMap<String, ExampleSpringService.Product>) categoryMapField.get(service);
            categoryMap.add("Electronics", new ExampleSpringService.Product("Laptop"));
            categoryMap.add("Electronics", new ExampleSpringService.Product("Smartphone"));
            categoryMap.add("Books", new ExampleSpringService.Product("Java Programming"));
        } catch (NoSuchFieldException | IllegalAccessException e) {
            fail("Failed to set up categoryMap: " + e.getMessage());
        }

        Set<Map.Entry<String, List<ExampleSpringService.Product>>> entries = service.getCategoryEntries();
        assertEquals(2, entries.size(), "There should be 2 categories");

        for (Map.Entry<String, List<ExampleSpringService.Product>> entry : entries) {
            String category = entry.getKey();
            List<ExampleSpringService.Product> products = entry.getValue();
            if ("Electronics".equals(category)) {
                assertEquals(2, products.size(), "Electronics should have 2 products");
                assertTrue(products.stream().anyMatch(p -> "Laptop".equals(p.getName())), "Electronics should contain Laptop");
                assertTrue(products.stream().anyMatch(p -> "Smartphone".equals(p.getName())), "Electronics should contain Smartphone");
            } else if ("Books".equals(category)) {
                assertEquals(1, products.size(), "Books should have 1 product");
                assertEquals("Java Programming", products.get(0).getName(), "Books should contain Java Programming");
            } else {
                fail("Unexpected category: " + category);
            }
        }
    }

    @Test
    public void testGetCategoryEntries_UnmodifiableSet() {
        // Add a category using reflection
        try {
            Field categoryMapField = ExampleSpringService.class.getDeclaredField("categoryMap");
            categoryMapField.setAccessible(true);
            @SuppressWarnings("unchecked")
            MultiValueMap<String, ExampleSpringService.Product> categoryMap =
                    (MultiValueMap<String, ExampleSpringService.Product>) categoryMapField.get(service);
            categoryMap.add("Toys", new ExampleSpringService.Product("Action Figure"));
        } catch (NoSuchFieldException | IllegalAccessException e) {
            fail("Failed to set up categoryMap: " + e.getMessage());
        }

        Set<Map.Entry<String, List<ExampleSpringService.Product>>> entries = service.getCategoryEntries();
        // Expect an UnsupportedOperationException when trying to add to the unmodifiable set
        assertThrows(UnsupportedOperationException.class, () -> entries.add(
                new AbstractMap.SimpleEntry<>("NewCategory", List.of())),
                "Adding to the entries set should throw UnsupportedOperationException");
    }

    @Test
    public void testGetCategoryEntries_UnmodifiableList() {
        // Add a category using reflection
        try {
            Field categoryMapField = ExampleSpringService.class.getDeclaredField("categoryMap");
            categoryMapField.setAccessible(true);
            @SuppressWarnings("unchecked")
            MultiValueMap<String, ExampleSpringService.Product> categoryMap =
                    (MultiValueMap<String, ExampleSpringService.Product>) categoryMapField.get(service);
            categoryMap.add("Groceries", new ExampleSpringService.Product("Apple"));
        } catch (NoSuchFieldException | IllegalAccessException e) {
            fail("Failed to set up categoryMap: " + e.getMessage());
        }

        Set<Map.Entry<String, List<ExampleSpringService.Product>>> entries = service.getCategoryEntries();
        for (Map.Entry<String, List<ExampleSpringService.Product>> entry : entries) {
            if ("Groceries".equals(entry.getKey())) {
                List<ExampleSpringService.Product> products = entry.getValue();
                // Verify that the list is modifiable
                assertDoesNotThrow(() -> products.add(new ExampleSpringService.Product("Banana")),
                        "Adding to the products list should not throw an exception");
                // Verify that the product was added
                assertTrue(products.stream().anyMatch(p -> "Banana".equals(p.getName())),
                        "Groceries should contain Banana after addition");
            }
        }
    }
}

