import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;

import org.springframework.beans.factory.config.BeanDefinitionHolder;
import org.springframework.beans.factory.support.GenericBeanDefinition;

public class ExampleSpringServiceTest {

    @Test
    public void testBuildBeanDescription_WithSingleAlias() {
        // Arrange
        GenericBeanDefinition beanDefinition = new GenericBeanDefinition();
        beanDefinition.setBeanClass(Object.class);
        String primaryName = "primaryBean";
        String[] aliases = {"alias1"};
        BeanDefinitionHolder holder = new BeanDefinitionHolder(beanDefinition, primaryName, aliases);
        ExampleSpringService service = new ExampleSpringService();

        // Act
        String description = service.buildBeanDescription(holder);

        // Assert
        assertEquals(holder.getShortDescription(), description);
    }

    @Test
    public void testBuildBeanDescription_WithMultipleAliases() {
        // Arrange
        GenericBeanDefinition beanDefinition = new GenericBeanDefinition();
        beanDefinition.setBeanClass(Object.class);
        String primaryName = "primaryBean";
        String[] aliases = {"alias1", "alias2", "alias3"};
        BeanDefinitionHolder holder = new BeanDefinitionHolder(beanDefinition, primaryName, aliases);
        ExampleSpringService service = new ExampleSpringService();

        // Act
        String description = service.buildBeanDescription(holder);

        // Assert
        assertEquals(holder.getShortDescription(), description);
    }

    @Test
    public void testBuildBeanDescription_WithNoAliases() {
        // Arrange
        GenericBeanDefinition beanDefinition = new GenericBeanDefinition();
        beanDefinition.setBeanClass(Object.class);
        String primaryName = "primaryBean";
        String[] aliases = {};
        BeanDefinitionHolder holder = new BeanDefinitionHolder(beanDefinition, primaryName, aliases);
        ExampleSpringService service = new ExampleSpringService();

        // Act
        String description = service.buildBeanDescription(holder);

        // Assert
        assertEquals(holder.getShortDescription(), description);
    }

    @Test
    public void testBuildBeanDescription_WithEmptyDescription() {
        // Arrange
        ExampleSpringService service = new ExampleSpringService();
        BeanDefinitionHolder holder = new BeanDefinitionHolder(new GenericBeanDefinition(), "primaryBean", new String[]{}) {
            @Override
            public String getShortDescription() {
                return "";
            }
        };

        // Act
        String description = service.buildBeanDescription(holder);

        // Assert
        assertEquals("", description);
    }

    @Test
    public void testBuildBeanDescription_WithNullDescription() {
        // Arrange
        ExampleSpringService service = new ExampleSpringService();
        BeanDefinitionHolder holder = new BeanDefinitionHolder(new GenericBeanDefinition(), "primaryBean", new String[]{}) {
            @Override
            public String getShortDescription() {
                return null;
            }
        };

        // Act
        String description = service.buildBeanDescription(holder);

        // Assert
        assertEquals(null, description);
    }

    @Test
    public void testBuildBeanDescription_NullHolder() {
        // Arrange
        ExampleSpringService service = new ExampleSpringService();

        // Act & Assert
        assertThrows(NullPointerException.class, () -> {
            service.buildBeanDescription(null);
        });
    }
}

