import org.springframework.beans.factory.config.BeanDefinitionHolder;

public class ExampleSpringService {
    public String buildBeanDescription(BeanDefinitionHolder holder) {
        String desc = holder.getShortDescription();
        String name = holder.getBeanName();
        String[] aliases = holder.getAliases();

        String base = (desc != null) ? desc : "";
        if (base.trim().isEmpty()) {
            base = (name != null) ? name : "";
        }

        if (aliases != null && aliases.length > 0) {
            StringBuilder sb = new StringBuilder(base);
            sb.append(" (aliases: ");
            for (int i = 0; i < aliases.length; i++) {
                if (i > 0) sb.append(", ");
                sb.append(aliases[i]);
            }
            sb.append(")");
            return sb.toString();
        }
        return base;
    }
}

