import org.junit.jupiter.api.Test;
import static org.junit.jupiter.api.Assertions.*;
import java.util.Set;
import java.util.concurrent.*;
import java.util.List;
import java.util.stream.Collectors;
import java.util.stream.IntStream;

public class ExampleSpringServiceTest {

    @Test
    public void testCreateUniqueMessageIdSequential() {
        ExampleSpringService service = new ExampleSpringService();
        String id1 = service.createUniqueMessageId();
        String id2 = service.createUniqueMessageId();
        String id3 = service.createUniqueMessageId();

        assertNotNull(id1, "First ID should not be null");
        assertNotNull(id2, "Second ID should not be null");
        assertNotNull(id3, "Third ID should not be null");

        assertTrue(id1.startsWith("msg-"), "First ID should start with 'msg-'");
        assertTrue(id2.startsWith("msg-"), "Second ID should start with 'msg-'");
        assertTrue(id3.startsWith("msg-"), "Third ID should start with 'msg-'");

        assertNotEquals(id1, id2, "First and second IDs should be unique");
        assertNotEquals(id1, id3, "First and third IDs should be unique");
        assertNotEquals(id2, id3, "Second and third IDs should be unique");

        // Verify that the numeric part is incremental
        int num1 = Integer.parseInt(id1.substring(4));
        int num2 = Integer.parseInt(id2.substring(4));
        int num3 = Integer.parseInt(id3.substring(4));

        assertEquals(num1 + 1, num2, "Second ID should be incremented by 1");
        assertEquals(num2 + 1, num3, "Third ID should be incremented by 1");
    }

    @Test
    public void testCreateUniqueMessageIdConcurrent() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int threadCount = 100;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        Set<String> ids = ConcurrentHashMap.newKeySet();

        List<Callable<String>> tasks = IntStream.range(0, threadCount)
            .mapToObj(i -> (Callable<String>) () -> service.createUniqueMessageId())
            .collect(Collectors.toList());

        List<Future<String>> futures = executor.invokeAll(tasks);
        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.MINUTES);

        for (Future<String> future : futures) {
            String id = future.get();
            assertNotNull(id, "Generated ID should not be null");
            assertTrue(id.startsWith("msg-"), "Generated ID should start with 'msg-'");
            ids.add(id);
        }

        assertEquals(threadCount, ids.size(), "All generated IDs should be unique");
    }
}

