import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;
import org.springframework.beans.factory.support.AutowireCandidateQualifier;
import org.springframework.beans.factory.support.GenericBeanDefinition;

import java.lang.annotation.Annotation;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private GenericBeanDefinition beanDefinition;
    private ExampleSpringService exampleSpringService;

    @BeforeEach
    public void setUp() {
        beanDefinition = new GenericBeanDefinition();
        exampleSpringService = new ExampleSpringService(beanDefinition);
    }

    @Test
    public void testContainsAttribute_WhenAttributeIsPresent() {
        // Given
        Class<? extends Annotation> attributeType = Deprecated.class;
        beanDefinition.addQualifier(new AutowireCandidateQualifier(attributeType.getName()));

        // When
        boolean result = exampleSpringService.containsAttribute(attributeType);

        // Then
        assertTrue(result, "The attribute should be present.");
    }

    @Test
    public void testContainsAttribute_WhenAttributeIsAbsent() {
        // Given
        Class<? extends Annotation> attributeType = Override.class;
        // No qualifier added

        // When
        boolean result = exampleSpringService.containsAttribute(attributeType);

        // Then
        assertFalse(result, "The attribute should be absent.");
    }

    @Test
    public void testContainsAttribute_WithCustomAnnotation_Present() {
        // Given
        Class<? extends Annotation> attributeType = CustomAnnotation.class;
        beanDefinition.addQualifier(new AutowireCandidateQualifier(attributeType.getName()));

        // When
        boolean result = exampleSpringService.containsAttribute(attributeType);

        // Then
        assertTrue(result, "The custom attribute should be present.");
    }

    @Test
    public void testContainsAttribute_WithCustomAnnotation_Absent() {
        // Given
        Class<? extends Annotation> attributeType = CustomAnnotation.class;
        // No qualifier added

        // When
        boolean result = exampleSpringService.containsAttribute(attributeType);

        // Then
        assertFalse(result, "The custom attribute should be absent.");
    }

    @Test
    public void testContainsAttribute_WithNullAttributeType() {
        // Given
        Class<? extends Annotation> attributeType = null;

        // When & Then
        assertThrows(NullPointerException.class, () -> {
            exampleSpringService.containsAttribute(attributeType);
        }, "Passing null as attributeType should throw NullPointerException.");
    }

    // Define a custom annotation for testing purposes
    private @interface CustomAnnotation {}
}

