        // Arrange
        Executor realExecutor = Runnable::run;
        AsyncUncaughtExceptionHandler realHandler = (throwable, method, params) -> {
            // Handle exception
        };

        Supplier<Executor> executorSupplier = () -> realExecutor;
        Supplier<AsyncUncaughtExceptionHandler> handlerSupplier = () -> realHandler;

        // Act
        exampleService.configureDataImportAsync(executorSupplier, handlerSupplier);

        // Assert
        AsyncAnnotationBeanPostProcessor asyncProcessor = getAsyncProcessor(exampleService);
        assertNotNull(asyncProcessor, "AsyncAnnotationBeanPostProcessor should not be null");

        Field executorSupplierField = AsyncAnnotationBeanPostProcessor.class.getDeclaredField("executor");
        executorSupplierField.setAccessible(true);
        @SuppressWarnings("unchecked")
        Supplier<? extends Executor> configuredExecutorSupplier =
                (Supplier<? extends Executor>) executorSupplierField.get(asyncProcessor);
        assertNotNull(configuredExecutorSupplier, "Executor supplier should not be null");
        Executor configuredExecutor = configuredExecutorSupplier.get();
        assertEquals(realExecutor, configuredExecutor, "Executor should be configured correctly");

        Field exceptionHandlerField = AsyncAnnotationBeanPostProcessor.class.getDeclaredField("exceptionHandler");
        exceptionHandlerField.setAccessible(true);
        @SuppressWarnings("unchecked")
        Supplier<? extends AsyncUncaughtExceptionHandler> configuredHandlerSupplier =
