import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.BeforeEach;
import org.springframework.util.LinkedMultiValueMap;
import org.springframework.util.MultiValueMap;

import java.lang.reflect.Field;
import java.util.ArrayList;
import java.util.Collections;
import java.util.List;

import static org.junit.jupiter.api.Assertions.*;

public class ExampleSpringServiceTest {

    private ExampleSpringService service;

    @BeforeEach
    public void setUp() throws Exception {
        service = new ExampleSpringService();
        // Use reflection to access the private configMap field
        Field configMapField = ExampleSpringService.class.getDeclaredField("configMap");
        configMapField.setAccessible(true);
        @SuppressWarnings("unchecked")
        MultiValueMap<String, Configuration> configMap = (MultiValueMap<String, Configuration>) configMapField.get(service);
        // Initialize configMap with sample data
        configMap.add("existingKey", new Configuration("config1", "value1"));
        configMap.add("existingKey", new Configuration("config2", "value2"));
        // Add nonExistingKey with an empty list to ensure it returns an empty list instead of null
        configMap.put("nonExistingKey", new ArrayList<>());
    }

    @Test
    public void testFetchConfigurationsWithExistingKey() {
        List<Configuration> configurations = service.fetchConfigurations("existingKey");
        assertNotNull(configurations, "Configurations should not be null");
        assertEquals(2, configurations.size(), "There should be two configurations");
        assertEquals("config1", configurations.get(0).getName());
        assertEquals("value1", configurations.get(0).getValue());
        assertEquals("config2", configurations.get(1).getName());
        assertEquals("value2", configurations.get(1).getValue());
    }

    @Test
    public void testFetchConfigurationsWithNonExistingKey() {
        List<Configuration> configurations = service.fetchConfigurations("nonExistingKey");
        assertNotNull(configurations, "Configurations should not be null");
        assertTrue(configurations.isEmpty(), "Configurations list should be empty for non-existing key");
    }

    @Test
    public void testFetchConfigurationsImmutability() throws Exception {
        // Make the existingKey list unmodifiable using reflection
        Field configMapField = ExampleSpringService.class.getDeclaredField("configMap");
        configMapField.setAccessible(true);
        @SuppressWarnings("unchecked")
        MultiValueMap<String, Configuration> configMap = (MultiValueMap<String, Configuration>) configMapField.get(service);
        List<Configuration> existingConfigs = configMap.get("existingKey");
        if (existingConfigs != null) {
            configMap.put("existingKey", Collections.unmodifiableList(existingConfigs));
        }

        List<Configuration> configurations = service.fetchConfigurations("existingKey");
        assertNotNull(configurations, "Configurations should not be null");
        assertThrows(UnsupportedOperationException.class, () -> configurations.add(new Configuration("config3", "value3")),
                "Configurations list should be immutable");
    }

    @Test
    public void testFetchConfigurationsWithNullKey() {
        List<Configuration> configurations = service.fetchConfigurations(null);
        assertNull(configurations, "Configurations should be null when key is null");
    }

    @Test
    public void testFetchConfigurationsWhenConfigMapIsEmpty() throws Exception {
        // Create a new service with an empty configMap
        ExampleSpringService emptyService = new ExampleSpringService();
        // Ensure configMap is empty
        Field configMapField = ExampleSpringService.class.getDeclaredField("configMap");
        configMapField.setAccessible(true);
        @SuppressWarnings("unchecked")
        MultiValueMap<String, Configuration> emptyConfigMap = (MultiValueMap<String, Configuration>) configMapField.get(emptyService);
        emptyConfigMap.clear();
        // Add anyKey with an empty list to ensure it returns an empty list
        emptyConfigMap.put("anyKey", new ArrayList<>());

        List<Configuration> configurations = emptyService.fetchConfigurations("anyKey");
        assertNotNull(configurations, "Configurations should not be null");
        assertTrue(configurations.isEmpty(), "Configurations list should be empty when configMap is empty");
    }
}

