import java.lang.annotation.Annotation;
import java.util.function.Predicate;
import org.springframework.core.annotation.MergedAnnotation;
import org.springframework.core.annotation.MergedAnnotations;
import org.springframework.lang.Nullable;

public class ExampleSpringService {
    private final Class<?> beanClass;

    public ExampleSpringService(Class<?> beanClass) {
        this.beanClass = beanClass;
    }

    public <A extends Annotation> MergedAnnotation<A> getAnnotation(String annotationType, @Nullable Predicate<? super MergedAnnotation<A>> condition) {
        if (annotationType == null) {
            throw new IllegalArgumentException("annotationType must not be null");
        }
        Class<?> resolvedClass;
        try {
            resolvedClass = Class.forName(annotationType, false, this.beanClass.getClassLoader());
        } catch (ClassNotFoundException ex) {
            throw new IllegalArgumentException(ex);
        }
        if (!Annotation.class.isAssignableFrom(resolvedClass)) {
            throw new IllegalArgumentException("Provided type is not an annotation: " + annotationType);
        }
        @SuppressWarnings("unchecked")
        Class<A> annotationClass = (Class<A>) resolvedClass;
        MergedAnnotations mergedAnnotations = MergedAnnotations.from(this.beanClass);
        return mergedAnnotations.get(annotationClass, condition);
    }
}

