import java.util.*;

public class ExampleSpringServiceTest {

    @Test
    public void testSequentialUniqueIds() {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfIds = 1000;
        Set<String> ids = new HashSet<>();

        for (int i = 1; i <= numberOfIds; i++) {
            String id = service.generateUniqueMessageId();
            assertNotNull(id, "Generated ID should not be null");
            assertEquals(String.valueOf(i), id, "IDs should be sequential and match the expected value");
            ids.add(id);
        }

        assertEquals(numberOfIds, ids.size(), "All generated IDs should be unique");
    }

    @Test
    public void testConcurrentUniqueIds() throws InterruptedException, ExecutionException {
        ExampleSpringService service = new ExampleSpringService();
        int numberOfThreads = 50;
        int idsPerThread = 1000;
        ExecutorService executor = Executors.newFixedThreadPool(numberOfThreads);
        Set<String> ids = ConcurrentHashMap.newKeySet();
        List<Callable<Void>> tasks = new ArrayList<>();

        for (int i = 0; i < numberOfThreads; i++) {
            tasks.add(() -> {
                for (int j = 0; j < idsPerThread; j++) {
                    String id = service.generateUniqueMessageId();
                    assertNotNull(id, "Generated ID should not be null");
                    ids.add(id);
                }
                return null;
            });
        }

        List<Future<Void>> futures = executor.invokeAll(tasks);
        executor.shutdown();
        executor.awaitTermination(1, TimeUnit.MINUTES);

        for (Future<Void> future : futures) {
            future.get();
        }

        assertEquals(numberOfThreads * idsPerThread, ids.size(), "All generated IDs should be unique under concurrency");
    }

    @Test
    public void testIdFormat() {
        ExampleSpringService service = new ExampleSpringService();
        String id = service.generateUniqueMessageId();
        assertTrue(id.matches("\\d+"), "ID should contain only digits");
    }

    @Test
    public void testIdStartsAtOne() {
        ExampleSpringService service = new ExampleSpringService();
        String firstId = service.generateUniqueMessageId();
        assertEquals("1", firstId, "First generated ID should be '1'");
    }
}

